import React, { useContext } from 'react';
import './CartItems.css';
import { ShopContext } from '../../Context/ShopContext';
import remove_icon from '../Assets/cart_cross_icon.png';

const CartItems = () => {
    const { all_product, cartItem, removeFromCart } = useContext(ShopContext);

    return (
        <div className='cartitems'>
            <div className="cartitems-format-main">
                <p>Products</p>
                <p>Title</p>
                <p>Price</p>
                <p>Quantity</p>
                <p>Total</p>
                <p>Remove</p>
            </div>
            <hr />
            {all_product.map((product) => {
                if (cartItem[product.id] > 0) {
                    return (
                        <div className="cartitems-format cartitems-format-main" key={product.id}>
                            <img src={product.image} alt="" className="carticon-product-icon" />
                            <p>{product.name}</p>
                            <p>${product.new_price}</p>
                            <button className='cartitems-quantity'>{cartItem[product.id]}</button>
                            <p>${product.new_price * cartItem[product.id]}</p>
                            <img className='cartitems-remove-icon' src={remove_icon} onClick={() => { removeFromCart(product.id) }} alt="" />
                        </div>
                    );
                }
                return null;
            })}
            <div className="cartitems-down">
                <div className="cartitems-total">
                    <h1>Cart Totals</h1>
                    <div>
                        div.cartitems-total-items
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CartItems;
